let userScore = 0;
let compScore = 0;

const choices = document.querySelectorAll(".choice");
const msg = document.querySelector("#msg");
const userScorePara = document.querySelector("#user-score");
const compScorePara = document.querySelector("#comp-score");
const resetBtn = document.querySelector("#reset-btn");

// Sound effects
const winSound = new Audio('data:audio/wav;base64,');
const loseSound = new Audio('data:audio/wav;base64,');
const drawSound = new Audio('data:audio/wav;base64,');

// Create confetti particles
const createConfetti = () => {
  const colors = ['#ffd700', '#ff0000', '#00ff00', '#0000ff', '#ff00ff'];
  for (let i = 0; i < 50; i++) {
    const confetti = document.createElement('div');
    confetti.className = 'confetti';
    confetti.style.backgroundColor = colors[Math.floor(Math.random() * colors.length)];
    confetti.style.left = Math.random() * 100 + 'vw';
    confetti.style.animationDuration = (Math.random() * 3 + 2) + 's';
    confetti.style.opacity = Math.random();
    document.body.appendChild(confetti);

    // Remove confetti after animation
    setTimeout(() => {
      confetti.remove();
    }, 5000);
  }
};

const genComputerChoice = () => {
  const options = ["rock", "paper", "scissor"];
  return options[Math.floor(Math.random() * 3)];
};

const resetChoices = () => {
  choices.forEach(choice => {
    choice.classList.remove('selected');
  });
};

const updateScoreDisplay = () => {
  userScorePara.textContent = userScore;
  compScorePara.textContent = compScore;
};

const showResult = (userWin, userChoice, computerChoice) => {
  resetChoices();
  document.querySelector(`#${userChoice}`).classList.add('selected');

  if (userWin === null) {
    msg.className = 'msg-draw';
    msg.textContent = `It's a Draw! Both chose ${userChoice}`;
    drawSound.play();
  } else if (userWin) {
    msg.className = 'msg-win';
    msg.textContent = `You Win! ${userChoice} beats ${computerChoice}`;
    winSound.play();
    userScore++;
    createConfetti(); // Add celebration effect
    
    // Add victory animation to the score
    userScorePara.classList.add('score-bump');
    setTimeout(() => {
      userScorePara.classList.remove('score-bump');
    }, 300);
  } else {
    msg.className = 'msg-lose';
    msg.textContent = `You Lose! ${computerChoice} beats ${userChoice}`;
    loseSound.play();
    compScore++;
  }
  
  updateScoreDisplay();
};

const playGame = (userChoice) => {
  const computerChoice = genComputerChoice();
  
  if (userChoice === computerChoice) {
    showResult(null, userChoice, computerChoice);
  } else {
    let userWin = true;
    if (userChoice === "rock") {
      userWin = computerChoice === "scissor";
    } else if (userChoice === "paper") {
      userWin = computerChoice === "rock";
    } else {
      userWin = computerChoice === "paper";
    }
    showResult(userWin, userChoice, computerChoice);
  }
};

const resetGame = () => {
  userScore = 0;
  compScore = 0;
  updateScoreDisplay();
  msg.className = '';
  msg.textContent = "Choose your weapon!";
  resetChoices();
};

// Event Listeners
choices.forEach((choice) => {
  choice.addEventListener("click", () => {
    const userChoice = choice.getAttribute("id");
    playGame(userChoice);
  });
});

resetBtn.addEventListener("click", resetGame);

// Add hover sound effect
choices.forEach(choice => {
  choice.addEventListener('mouseenter', () => {
    choice.style.transform = 'scale(1.1)';
  });
  
  choice.addEventListener('mouseleave', () => {
    choice.style.transform = 'scale(1)';
  });
});
